mapper.select:
  	SELECT * FROM chargetype WHERE isvalid = 1 AND (LOCATE(#{contant,jdbcType=VARCHAR},typecode)
  	OR LOCATE(#{contant,jdbcType=VARCHAR},typename))

		limit 0, #{pagesize,jdbcType=INTEGER}


		limit ${(currentpage-1)*pagesize},${pagesize}

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into chargetype (typecode, typename, itemmark, 
  discount, note, isvalid
  )
values (#{typecode,jdbcType=VARCHAR}, #{typename,jdbcType=VARCHAR}, #{itemmark,jdbcType=VARCHAR}, 
  #{discount,jdbcType=INTEGER}, #{note,jdbcType=VARCHAR}, #{isvalid,jdbcType=INTEGER}
  )
