mapper.select:
SELECT d.*,c.typename AS chargetypename,a1.detailname AS drugtypename,a2.detailname AS agenttypename, a3.detailname AS medicaretypename,s.suppliername FROM drug d LEFT JOIN chargetype c ON c.id = d.chargetypeid LEFT JOIN attributedetail a1 ON a1.id = d.drugtypeid LEFT JOIN attributedetail a2 ON a2.id = d.agenttypeid LEFT JOIN attributedetail a3 ON a3.id = d.medicaretypeid LEFT JOIN supplier s ON s.id = d.supplierid WHERE d.isvalid = 1 AND LOCATE(#{vo.drugname,jdbcType=VARCHAR},d.drugname) AND LOCATE(#{vo.drugcode,jdbcType=VARCHAR},d.drugcode) AND LOCATE(#{vo.medicalsciencecode,jdbcType=VARCHAR},d.medicalsciencecode) AND LOCATE(#{vo.pinyincode,jdbcType=VARCHAR},d.pinyincode) AND LOCATE(#{vo.manufacturer,jdbcType=VARCHAR},d.manufacturer) AND LOCATE(CASE WHEN #{vo.supplierid,jdbcType=INTEGER} = 0 THEN '' ELSE #{vo.supplierid,jdbcType=INTEGER} END,d.supplierid) AND LOCATE(CASE WHEN #{vo.usestatus,jdbcType=INTEGER} = 0 THEN '' ELSE #{vo.usestatus,jdbcType=INTEGER} END,d.usestatus) AND LOCATE(CASE WHEN #{vo.chargetypeid,jdbcType=INTEGER} = 0 THEN '' ELSE #{vo.chargetypeid,jdbcType=INTEGER} END,d.chargetypeid)
limit 0, #{pagesize,jdbcType=INTEGER}
limit ${(currentpage-1)*pagesize},${pagesize}
