mapper.select:
SELECT m.*, e.employeename,dt.departmentname,d.diseasename FROM medicalrecordtemplate m LEFT JOIN employee e ON e.id = m.doctorid LEFT JOIN department dt ON dt.id = e.departmentid LEFT JOIN disease d ON d.id = m.diseaseid WHERE m.isvalid = 1 AND LOCATE(#{vo.templatecode,jdbcType=VARCHAR}, templatecode) AND LOCATE(#{vo.templatename,jdbcType=VARCHAR},templatename) AND LOCATE(CASE WHEN #{vo.doctorid,jdbcType=INTEGER} = 0 THEN '' ELSE #{vo.doctorid,jdbcType=INTEGER} END,doctorid) AND LOCATE(CASE WHEN #{vo.departmentid,jdbcType=INTEGER} = 0 THEN '' ELSE #{vo.departmentid,jdbcType=INTEGER} END,dt.id) AND LOCATE(#{vo.fitstatus,jdbcType=VARCHAR},fitstatus)
limit 0, #{pagesize,jdbcType=INTEGER}
limit ${(currentpage-1)*pagesize},${pagesize}
