mapper.select:
SELECT d.*,c.chargestatus,p.id AS prescriptionid,p.registerid,p.prescriptioncode,pt.patientname,e.employeename FROM dispensing d LEFT JOIN charge c ON c.id = d.chargeid LEFT JOIN prescription p ON p.id = c.prescriptionid LEFT JOIN register r ON r.id = p.registerid LEFT JOIN patient pt ON pt.id = r.patientid LEFT JOIN employee e ON e.id = p.doctorid LEFT JOIN department dt ON dt.id = e.departmentid WHERE d.isvalid = 1 AND LOCATE(#{vo.prescriptioncode,jdbcType=VARCHAR},p.prescriptioncode) AND LOCATE(#{vo.cardcode,jdbcType=VARCHAR},pt.cardcode) AND LOCATE(#{vo.patientname,jdbcType=VARCHAR},pt.patientname) AND LOCATE(CASE WHEN #{vo.departmentid,jdbcType=INTEGER} = 0 THEN '' ELSE #{vo.departmentid,jdbcType=INTEGER} END,e.departmentid) AND LOCATE(CASE WHEN #{vo.doctorid,jdbcType=INTEGER} = 0 THEN '' ELSE #{vo.doctorid,jdbcType=INTEGER} END,p.doctorid) AND LOCATE(CASE WHEN #{vo.dispensingstatus,jdbcType=INTEGER} = 0 THEN '' ELSE #{vo.dispensingstatus,jdbcType=INTEGER} END, CASE WHEN d.dispensingstatus IS NULL THEN 1 ELSE d.dispensingstatus END) AND CASE WHEN #{vo.begintime,jdbcType=VARCHAR} = '' THEN 1=1 ELSE CASE WHEN d.dispensingtime IS NULL THEN c.chargetime ELSE d.dispensingtime END BETWEEN #{vo.begintime,jdbcType=VARCHAR} AND #{vo.endtime,jdbcType=VARCHAR} END ORDER BY dispensingstatus,id ASC
limit 0, #{pagesize,jdbcType=INTEGER}
limit ${(currentpage-1)*pagesize},${pagesize}

mapper.update:
update dispensing set dispensingstatus = #{dispensingstatus,jdbcType=INTEGER},
dispensingtime = #{dispensingtime,jdbcType=TIMESTAMP} 
where chargeid = #{chargeid,jdbcType=INTEGER}
