mapper.select:
SELECT m.*,r.registercode,p.cardcode,p.patientname,e.employeename,dt.departmentname,d.diseasename FROM medicalrecord m LEFT JOIN register r ON r.id = m.registerid LEFT JOIN patient p ON p.id = r.patientid LEFT JOIN employee e ON e.id = m.doctorid LEFT JOIN department dt ON dt.id = e.departmentid LEFT JOIN disease d ON d.id = m.diseaseid WHERE m.isvalid = 1 AND R.isvalid = 1 AND LOCATE(#{vo.cardcode,jdbcType=VARCHAR}, cardcode) AND LOCATE(#{vo.patientname,jdbcType=VARCHAR},patientname) AND LOCATE(#{vo.medicalrecordname,jdbcType=VARCHAR},medicalrecordname) AND LOCATE(CASE WHEN #{vo.doctorid,jdbcType=INTEGER} = 0 THEN '' ELSE #{vo.doctorid,jdbcType=INTEGER} END,m.doctorid) AND LOCATE(CASE WHEN #{vo.departmentid,jdbcType=INTEGER} = 0 THEN '' ELSE #{vo.departmentid,jdbcType=INTEGER} END,dt.id)
limit 0, #{pagesize,jdbcType=INTEGER}
limit ${(currentpage-1)*pagesize},${pagesize}
