mapper.select:
SELECT e.*,a.detailname AS jobtitlename,a1.detailname AS positionname,a2.detailname AS maxeducationname, a3.detailname AS majorname,a4.detailname AS politicsstatusname,d.departmentname FROM employee e LEFT JOIN department d ON d.id = e.departmentid LEFT JOIN attributedetail a ON a.id = e.jobtitleid LEFT JOIN attributedetail a1 ON a1.id = e.positionid LEFT JOIN attributedetail a2 ON a2.id = e.maxeducationid LEFT JOIN attributedetail a3 ON a3.id = e.majorid LEFT JOIN attributedetail a4 ON a4.id = e.politicsstatusid WHERE e.isvalid = 1 AND (LOCATE(#{contant,jdbcType=VARCHAR},e.employeecode) OR LOCATE(#{contant,jdbcType=VARCHAR},e.employeename) OR LOCATE(#{contant,jdbcType=VARCHAR},e.idcard))
limit 0, #{pagesize,jdbcType=INTEGER}
limit ${(currentpage-1)*pagesize},${pagesize}
