mapper.select:
SELECT r.*,p.cardcode,p.patientname,CASE WHEN r.registertypeid = 0 THEN '自定义' ELSE rt.typename END AS typename, rt.typesum,d.departmentname,e.employeename AS doctorname,e1.employeename AS registerpersonname FROM register r LEFT JOIN patient p ON p.id = r.patientid LEFT JOIN registertype rt ON rt.id = r.registertypeid LEFT JOIN department d ON d.id = r.departmentid LEFT JOIN employee e ON e.id = r.doctorid LEFT JOIN employee e1 ON e1.id = r.registerpersonid WHERE r.isvalid = 1 and registertime BETWEEN CURRENT_DATE AND DATE_ADD(CURRENT_DATE, INTERVAL 1 DAY) ORDER BY id DESC
limit 0, #{pagesize,jdbcType=INTEGER}
limit ${(currentpage-1)*pagesize},${pagesize}

SELECT r.*,p.*,a.detailname AS insuretypename,CASE WHEN r.registertypeid = 0 THEN '自定义' ELSE rt.typename END AS typename, rt.typesum,d.departmentname,e.employeename AS doctorname,e1.employeename AS registerpersonname FROM register r LEFT JOIN patient p ON p.id = r.patientid LEFT JOIN registertype rt ON rt.id = r.registertypeid LEFT JOIN department d ON d.id = r.departmentid LEFT JOIN employee e ON e.id = r.doctorid LEFT JOIN employee e1 ON e1.id = r.registerpersonid LEFT JOIN attributedetail a ON a.id = p.insuretypeid WHERE r.isvalid = 1 AND LOCATE(#{vo.cardcode,jdbcType=VARCHAR},cardcode) AND LOCATE(#{vo.patientname,jdbcType=VARCHAR},patientname) AND LOCATE((CASE WHEN #{vo.registerpersonid,jdbcType=INTEGER} = 0 THEN '' ELSE #{vo.registerpersonid,jdbcType=INTEGER} END),registerpersonid) AND LOCATE((CASE WHEN #{vo.registerstatus,jdbcType=INTEGER} = 0 THEN '' ELSE #{vo.registerstatus,jdbcType=INTEGER} END),registerstatus) AND UNIX_TIMESTAMP(registertime) >= UNIX_TIMESTAMP(CASE WHEN #{vo.begintime,jdbcType=VARCHAR} = '' THEN registertime ELSE #{vo.begintime,jdbcType=VARCHAR} END) AND UNIX_TIMESTAMP (registertime) <= UNIX_TIMESTAMP(CASE WHEN #{vo.endtime,jdbcType=VARCHAR} = '' THEN registertime ELSE DATE_ADD(#{vo.endtime,jdbcType=VARCHAR},INTERVAL 1 DAY) END) ORDER BY r.id DESC
limit 0, #{pagesize,jdbcType=INTEGER}
limit ${(currentpage-1)*pagesize},${pagesize}
